import { useState } from 'react';

const GuestForm = ({ addGuest }) => {

    const [name, setName] = useState("")
    const [email, setEmail] = useState("")
    const [checkedInStatus, setCheckedInStatus] = useState("")

    const handleNameChange = event => setName(event.target.value)
    const handleEmailChange = event => setEmail(event.target.value)
    const handlecheckedInStatusChange = event => setCheckedInStatus(event.target.value)


    const handleSubmit = event => {
        event.preventDefault()
        const guest = {
            name,
            email,
            checkedInStatus
        }
        createGuest(guest)
        setName("")
        setEmail("")
        setCheckedInStatus("")
    }

    return (
        <form onSubmit={handleSubmit}>
          <label htmlFor="name">Name:</label>
          <input 
            type="text" 
            id="name" 
            value={name} 
            onChange={handleNameChange} 
            required
          />

          <label htmlFor="playingTime">Playing Time:</label>
          <input 
            type="number" 
            id="playingTime" 
            value={playingTime} 
            onChange={handlePlayingTimeChange} 
            required
          />

          <label htmlFor="minNumPlayers">Min Players:</label>
          <input 
            type="number" 
            id="minNumPlayers" 
            value={minNumPlayers} 
            onChange={handleMinNumPlayersChange} 
            required
          />

          <label htmlFor="maxNumPlayers">Max Players:</label>
          <input 
            type="number" 
            id="maxNumPlayers" 
            value={maxNumPlayers} 
            onChange={handleMaxNumPlayersChange} 
            required
          />

          <input type="submit" name="submit" value="Save"/>
        </form>
      )

}

export default GuestForm
